import { nanoid } from 'nanoid/non-secure';
import { NodeImpl, nodeDefinition } from '../NodeImpl.js';
import { dedent } from 'ts-dedent';
export class PopNodeImpl extends NodeImpl {
    static create() {
        const baseNode = {
            type: 'pop',
            title: 'Pop',
            id: nanoid(),
            visualData: {
                x: 0,
                y: 0,
                width: 200,
            },
            data: {},
        };
        return baseNode;
    }
    getInputDefinitions() {
        return [
            {
                dataType: 'any[]',
                id: 'array',
                title: 'Array',
            },
        ];
    }
    getOutputDefinitions() {
        return [
            {
                dataType: 'any',
                id: 'lastItem',
                title: 'Last',
            },
            {
                dataType: 'any',
                id: 'restOfArray',
                title: 'Rest',
            },
        ];
    }
    static getUIData() {
        return {
            infoBoxBody: dedent `
        Pops the last value off the input array and outputs the new array and the popped value.

        Can also be used to just extract the last value from an array.
      `,
            infoBoxTitle: 'Pop Node',
            contextMenuTitle: 'Pop',
            group: ['Lists'],
        };
    }
    async process(inputs) {
        const inputArray = inputs['array']?.value;
        if (!Array.isArray(inputArray) || inputArray.length === 0) {
            throw new Error('Input array is empty or not an array');
        }
        const lastItem = inputArray[inputArray.length - 1];
        const rest = inputArray.slice(0, inputArray.length - 1);
        return {
            ['lastItem']: {
                type: 'any',
                value: lastItem,
            },
            ['restOfArray']: {
                type: 'any[]',
                value: rest,
            },
        };
    }
}
export const popNode = nodeDefinition(PopNodeImpl, 'Pop');
